import React, { useState } from "react";
import styled, { createGlobalStyle } from "styled-components";
import MovieComponent from "./components/MovieComponent";

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    margin: 0;
    padding: 0;
    font-family: "Josefin Sans", sans-serif;
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;

const Header = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  background-color: black;
  color: white;
  padding: 10px;
  font-size: 25px;
  font-weight: bold;
  box-shadow: 0 3px 6px 0 #555;
  align-items: center;
`;

const AppName = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const MovieImage = styled.img`
  width: 48px;
  height: 48px;
  margin: 15px;
`;
const SearchBox = styled.div`
  display: flex;
  flex-direction: row;
  padding: 10px 10px;
  background-color: white;
  border-radius: 6px;
  margin-left: 20px;
  width: 50%;
  align-items: center;
`;
const SearchIcon = styled.img`
  width: 32px;
  height: 32px;
`;
const SearchInput = styled.input`
  color: black;
  font-size: 16px;
  font-weight: bold;
  border: none;
  outline: none;
  margin-left: 15px;
`;

const MovieListContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 30px;
  justify-content: space-evenly;
`;
function App() {
  const [searchQuery, updateSearchQuery] = useState();
  const[timeoutId,updateTimeoutId] = useState();

  const fetchData = (searchString) => {
    axios.get(`http://www.omdbapi.com/?s=${searchString}&apikey=1eccfc2e`)
  }
  const onTextChange = (event) => {
    clearTimeout(timeoutId);
    updateSearchQuery(event.target.value);
    const timeout = setTimeout(()=>"API CALL",500)
    updateTimeoutId(timeout);
  };

  return (
    <>
      <GlobalStyle />
      <Container>
        <Header>
          <AppName>
            <MovieImage src="https://freepnglogo.com/images/all_img/1707222563twitter-logo-png.png" />
            React Movie App
          </AppName>
          <SearchBox>
            <SearchIcon src="https://img.icons8.com/?size=100&id=132&format=png&color=1A1A1A" />
            <SearchInput
              placeholder="Search Movie"
              value={searchQuery}
              onChange={onTextChange}
            />
          </SearchBox>
        </Header>
        <MovieListContainer>
          <MovieComponent />
          <MovieComponent />
          <MovieComponent />
          <MovieComponent />
          <MovieComponent />
        </MovieListContainer>
      </Container>
    </>
  );
}

export default App;
