import React from 'react';
import styled, { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    margin: 0;
    padding: 0;
    font-family: "Josefin Sans", sans-serif;
  }
`;

const Container = styled.div `
display: flex;
flex-direction: column;
`;

const Header = styled.div `
  display: flex;
  flex-direction: row;
  background-color: black;
  color: white;
  padding: 10px;
  font-size: 25px;
  font-weight: bold;
  box-shadow: 0 3px 6px 0 #555;
  `;

  const AppName = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
  `;

  const MovieImage = styled.img `
    width: 48px;
    height: 48px;
    margin: 15px;
  `;
  const SearchBox = styled.div`
  display: flex;
  flex-direction: row;
  padding: 10px 10px;
  background-color: white;
  border-radius: 6px;
  margin-left:20px;
  `;
  const SearchIcon = styled.img `
  `;

function App() {
  return (
    <>
    <GlobalStyle/>
      <Container>
        <Header>
          <AppName>
          <MovieImage src="https://freepnglogo.com/images/all_img/1707222563twitter-logo-png.png"/>
            React Movie App
          </AppName>
          <SearchBox>
            <SearchIcon src = "https://img.icons8.com/?size=100&id=132&format=png&color=1A1A1A" />
          </SearchBox>
        </Header>
      </Container>
    </>
  )
}

export default App
