import styled from "styled-components"


const MovieContainer = styled.div `
display: flex;
flex-direction: column;
padding: 10px;
width: 280px;
box-shadow: 0 3px 10px 0 #aaa;
cursor:pointer;
`;

const CoverImage = styled.img`
object-fit: cover;
height: 362px;
`;

const MovieName = styled.span `
font-size:18px;
font-weight:600;
color: black;
margin: 15px 0;
white-space: nowrap;
text-overflow: ellipsis;
overflow: hidden;
`;

const InfoColumn = styled.div `
display: flex;
flex-direction: row;
justify-content: space-between;
`;


const MovieComponent = (props) => {
  return (
    <MovieContainer>
    <CoverImage src="https://www.omdbapi.com/src/poster.jpg" />
   <MovieName>Movie NameMovie NameMovie NameMovie NameMovie Name</MovieName>
   <InfoColumn>
    <span>2012</span>
    <span>Movie</span>
   </InfoColumn>
    </MovieContainer>
  )
}

export default MovieComponent
